    private void btnPrintActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPrintActionPerformed
        // TODO add your handling code here:
        try {
            StringBuilder content = new StringBuilder();
            int row = jTable1.getSelectedRow();
            String PaymentID = jTable1.getValueAt(row, 0).toString();
            String AppointmentID = jTable1.getValueAt(row, 1).toString();
            String CustomerName = jTable1.getValueAt(row, 2).toString();
            String CategoryName = jTable1.getValueAt(row, 3).toString();
            String CategoryPrice = jTable1.getValueAt(row, 4).toString();
            String AppointmentDate = jTable1.getValueAt(row, 5).toString();
            String AppointmentTime = jTable1.getValueAt(row, 6).toString();
            String ProductName = jTable1.getValueAt(row, 7).toString();
            String ProductPrice = jTable1.getValueAt(row, 8).toString();
            String ProductQuantity = jTable1.getValueAt(row, 9).toString();
            String DateAdded = jTable1.getValueAt(row, 10).toString();
            String Total =  jTable1.getValueAt(row, 11).toString();
            
            content.append("<center><img src=\"data:image/png;base64,\"/></center>");
            content.append("<center><h1>APU Automotive Service Centre Receipts</h1></center>");
            content.append("<center><h2>Asia Pacific University of Technology and Innovation<br></h2></center>");
            content.append("<center><h4>Technology Park Malaysia.<br></h4></center>");
            content.append("<center><h4>57000 Bukit Jalil, Malaysia.<br></h4></center>");
            content.append("<p><b>Payment ID: </b>").append(PaymentID).append("</p>");
            content.append("<p><b>Appointment ID: </b>").append(AppointmentID).append("</p>");
            content.append("<p><b>Member Name: </b>").append(CustomerName).append("</p>");
            content.append("<p><b>Category Name: </b>").append(CategoryName).append("</p>");
            content.append("<p><b>Category Price: </b>").append(CategoryPrice).append("</p>");
            content.append("<p><b>Appointment Date: </b>").append(AppointmentDate).append("</p>");
            content.append("<p><b>Appointment Time: </b>").append(AppointmentTime).append("</p>");
            content.append("<p><b>Product Name: </b>").append(ProductName).append("</p>");
            content.append("<p><b>Product Price: </b>").append(ProductPrice).append("</p>");
            content.append("<p><b>Product Quantity: </b>").append(ProductQuantity).append("</p>");
            content.append("<p><b>Date Added: </b>").append(DateAdded).append("</p>");
            content.append("<p><b>Total: </b>").append(Total).append("</p>");
            content.append("<br>");
            content.append("<p><b>Generated Date: </b>").append(new Date().toString()).append("</p>");
            content.append("<br>");
            content.append("<p><b>--------------------------------------------------Have a nice day--------------------------------------------------</b></p>");
            InputStream htmlSource = new ByteArrayInputStream(content.toString().getBytes(StandardCharsets.UTF_8));

            File pdfDest = new File(System.getProperty("user.dir") + "/receipt.pdf");
            ConverterProperties converterProperties = new ConverterProperties();
            
            try {
                HtmlConverter.convertToPdf(htmlSource, new FileOutputStream(pdfDest), converterProperties);
                JOptionPane.showMessageDialog(rootPane, "A new pdf file is generated.");
                
                int confirm = JOptionPane.showConfirmDialog(rootPane, "You want to sent E-receipt?", "E-Receipt", JOptionPane.YES_NO_OPTION);
                if (confirm == JOptionPane.YES_OPTION) {
                    MailUtil.sendReceipt(getCustomerEmail(CustomerName));
                }
                
            } catch (IOException e) {
                
            }
            
        } catch (Exception e) {
            JOptionPane.showMessageDialog(rootPane, "Something happen. Please try to launch the system again");
        }
    }//GEN-LAST:event_btnPrintActionPerformed

    void Display() {
        jTable1.setModel(new DefaultTableModel(null, new String[]{"Payment ID", "Appointment ID", "Customer Name", "Category Name", "Category Price", "Appointment Date", "Appointment Time", "Product Name", "Product Price", "Product Quantity", "Date Added", "Total", "Status"}));
        da.setFileName("Payment.txt");
        int row = 0;
        data = da.readAll();
        for (int i = 0; i < data.size(); i++) {//row
            String[] split = data.get(i).split("\\|");
            if (split[2].contains(txtSearch.getText().toUpperCase())) {
                DefaultTableModel model = (DefaultTableModel) jTable1.getModel();
                model.addRow(new String[]{"", "", "", "", "", "", "", "", "", "", "", "", ""});
                for (int j = 0; j < split.length; j++) {//col
                    jTable1.getModel().setValueAt(split[j], row, j);
                }
                row++;
            }
        }
    }
    
    private String getCustomerEmail(String CustomerName) {
        String email = null;
        DataAccess da = new DataAccess();
        da.setFileName("Customer.txt");
        ArrayList<String> data = da.readAll();
        for(String rec: data) {
            String[] split = rec.split("\\|");
            if(split[2].equals(CustomerName)) {
                email = split[5];
            }
        }
        return email;
    }
    
    
